import { useDrop } from 'react-dnd';

const DropZone = ({type, english, spanish, colourList}) => {
    const typeBoard = [];

    const [{isOver}, drop] = useDrop(() => ({
        // accept: type
        accept: type,
        drop: (item) => addItemToTypeBoard(item.id),
        collect: (monitor) => ({
            isOver: !!monitor.isOver(),
        }),
    }));

    const addItemToTypeBoard = (id) => {
        // console.log(id);
        const copyColourList = colourList.filter((colour) => id === colour.id);
        typeBoard.push(copyColourList[0]);
    }


    return (
        <div ref={drop} className="dropzone">
            <p className={english}>{spanish}</p>
        </div>
    );
}

export default DropZone;
