        copyBoard.push(copyColourList[0]);
        setBoard(copyBoard);
        console.log(board);
        console.log('board length is: ' + board.length)
    }


    // {board.map((colour) => {
    //     return <Colour name={colour.english} img={colour.img} id={colour.id} key={colour.id} />
    // })}


    return (
        <div ref={drop} className="dropzone">
            { typeBoard.length > 0 ? 
            <Colour name={name} />
            : <p className={name}>{spanish}</p>
            }
        </div>
    );
}

export default DropZone;
