import { useDrop } from 'react-dnd';

const DropZone = ({key, type, english, spanish, addItemToBoard}) => {

    const [{isOver}, drop] = useDrop(() => ({
        accept: type,
        drop: (item) => addItemToBoard(item.id),
        collect: (monitor) => ({
            isOver: !!monitor.isOver(),
        }),
    }));


    return (
        <p>colour</p>
    );
}

export default DropZone;
